var suggestions=document.getElementById("suggestions"),search=document.getElementById("search");search!==null&&document.addEventListener("keydown",inputFocus);function inputFocus(e){e.ctrlKey&&e.key==="/"&&(e.preventDefault(),search.focus()),e.key==="Escape"&&(search.blur(),suggestions.classList.add("d-none"))}document.addEventListener("click",function(e){var t=suggestions.contains(e.target);t||suggestions.classList.add("d-none")}),document.addEventListener("keydown",suggestionFocus);function suggestionFocus(n){const s=suggestions.classList.contains("d-none");if(s)return;const e=[...suggestions.querySelectorAll("a")];if(e.length===0)return;const t=e.indexOf(document.activeElement);if(n.key==="ArrowUp"){n.preventDefault();const s=t>0?t-1:0;e[s].focus()}else if(n.key==="ArrowDown"){n.preventDefault();const s=t+1<e.length?t+1:t;e[s].focus()}}(function(){var e=new FlexSearch.Document({tokenize:"forward",cache:100,document:{id:"id",store:["href","title","description"],index:["title","description","content"]}});e.add({id:0,href:"/docs/prologue/introduction/",title:"Introduction",description:"Plubo is a simple and fast WordPress plugin boilerplate with some useful scripts to speed up your development.",content:"âœ”ï¸ Use Blade views and directives from the start\nâœ”ï¸ Add styles in SCSS\nâœ”ï¸ JavaScript router lets you add scripts on specific WordPress templates (based on body tags) and on specific shortcode tags\nGet started #\rcomposer create-project joanrodas/plubo project-name  Help #\rGet help on the docs. Help â†’\n"}).add({id:1,href:"/docs/prologue/quick-start/",title:"Quick Start",description:"Summary of how to start using Plubo.",content:"Requirements #\r PHP \u0026gt;= 7.3 Composer node \u0026gt;= v12  Get PLUBO #\rThere are 2 options to start using Plubo:\n1. As a GitHub template #\rIf you intend to use GitHub for your project, your best option is to create a new repo using this one as a template, just clicking the Use this template button.\n When using Plubo as a template, a pipeline will be executed right after the repo creation, modifiyng the filenames and classes to match your new project name. The pipeline files will be autoremoved.\n 2. With composer #\rcomposer create-project joanrodas/plubo \u0026lt;PROJECT_NAME\u0026gt;\n After creating the project, use the command php plubo add to add Alpine.js, React and/or environment variables to your project.\n "}).add({id:2,href:"/docs/prologue/commands/",title:"Commands",description:"Plubo comes with commands for common tasks.",content:"add #\rAdd dependencies to your project:\nphp plubo add [option] [path]  remove #\rRemove dependencies from your project:\nphp plubo rm [option] [path]  "}).add({id:3,href:"/docs/structure/structure/",title:"Plugin structure",description:"Plubo is a simple and fast WordPress plugin boilerplate with some useful scripts to speed up your development.",content:"Simple structure #\rYour plugin | â”œâ”€â”€ admin â”‚ â”œâ”€â”€ admin-menus.php â”‚Â â”œâ”€â”€ admin.php â”‚Â â”œâ”€â”€ ajax-actions.php â”‚Â â”œâ”€â”€ crons.php â”‚Â â”œâ”€â”€ emails.php â”‚Â â””â”€â”€ post-actions.php â”œâ”€â”€ includes â”‚Â â”œâ”€â”€ activator.php â”‚Â â”œâ”€â”€ blade.php â”‚Â â”œâ”€â”€ deactivator.php â”‚Â â”œâ”€â”€ i18n.php â”‚Â â”œâ”€â”€ loader.php â”‚Â â””â”€â”€ utils.php â”œâ”€â”€ languages â”œâ”€â”€ plubo â”œâ”€â”€ plugin-placeholder.php â”œâ”€â”€ public â”‚Â â”œâ”€â”€ api-endpoints.php â”‚Â â”œâ”€â”€ custom-fields.php â”‚Â â”œâ”€â”€ custom-post-types.php â”‚Â â”œâ”€â”€ public.php â”‚Â â”œâ”€â”€ routes.php â”‚Â â”œâ”€â”€ shortcodes.php â”‚Â â””â”€â”€ taxonomies.php â”œâ”€â”€ react â”‚Â â”œâ”€â”€ apps â”‚Â â”œâ”€â”€ apps.php â”œâ”€â”€ resources â”‚Â â”œâ”€â”€ assets â”‚Â â”‚Â â”œâ”€â”€ fonts â”‚Â â”‚Â â”œâ”€â”€ scripts â”‚Â â”‚Â â””â”€â”€ styles â”‚Â â”œâ”€â”€ directives â”‚Â â””â”€â”€ views â””â”€â”€ uninstall.php  "}).add({id:4,href:"/docs/structure/scripts/",title:"Using JavaScript",description:"Plubo is a simple and fast WordPress plugin boilerplate with some useful scripts to speed up your development.",content:"Add Alpine.js to your project #\rYou can add Alpine.js with a simple command php plubo add alpine\n"}).add({id:5,href:"/docs/structure/styles/",title:"Styles",description:"Plubo is a simple and fast WordPress plugin boilerplate with some useful scripts to speed up your development.",content:"SCSS #\r"}).add({id:6,href:"/docs/structure/blade/",title:"Blade templates and direcives",description:"Summary of how to start using Plubo.",content:"Blade templates ready #\r"}).add({id:7,href:"/docs/structure/react/",title:"Creating React apps",description:"Plubo creates React apps boilerplates for you.",content:"Create a React app as a WordPress shortcode automagically! ðŸš€ðŸš€ðŸš€ #\rIf you need to use React in your project, Plubo can prepare a simple structure for you ðŸ˜ƒ\nphp plubo add react \u0026lt;APP_NAME\u0026gt;\n Note: If you don\u0026rsquo;t enter an app name, a random one will be assigned.\n "}).add({id:8,href:"/docs/routes/routing/",title:"Plubo Routes",description:"Creating routes.",content:"âœ”ï¸ No need to write rewrite rules and tags manually\nâœ”ï¸ Easily extendable with hooks\nâœ”ï¸ Ready to use with blade templates\n Getting started #\rcomposer require joanrodas/plubo-routes\n You can also install Plubo Routes as a standalone WordPress plugin, simply downloading the zip and placing it in the plugins folder.\n  Adding new routes #\r\u0026lt;?php use PluboRoutes\\PluboRoutesProcessor; use PluboRoutes\\Route; PluboRoutesProcessor::init(); add_filter('plubo/routes', function($routes) { $routes[] = new Route( 'route_name', 'example/{city:word}/{id:number}', 'template_path' ); return $routes; }); ?\u0026gt;  Available syntax: #\r number (numbers only) word (a-Z only) slug (a valid WordPress slug) date (yyyy-mm-dd date) year (4 digits) month (01-12) day (01-31) digit (single digit 0-9) jwt (JWT token) ip (IPv4)   You can also use custom regex patterns using the format {variable_name:regex_patter} like {author:([a-z0-9-]+)}\n  Route Actions #\rYou can execute your custom functions:\n\u0026lt;?php add_action('plubo/route_{route_name}', function() { #Execute code }); ?\u0026gt;  "}).add({id:9,href:"/docs/help/faq/",title:"FAQ",description:"Answers to frequently asked questions.",content:"WIP #\r"}).add({id:10,href:"/docs/help/",title:"Help",description:"Help Plubo.",content:""}).add({id:11,href:"/docs/routes/",title:"Routes",description:"Plubo Routes.",content:""}).add({id:12,href:"/docs/structure/",title:"Structure",description:"Plubo structure.",content:""}).add({id:13,href:"/docs/prologue/",title:"Prologue",description:"Prologue Plubo.",content:""}).add({id:14,href:"/docs/",title:"Docs",description:"Plubo Docs.",content:""}),search.addEventListener("input",t,!0);function t(){const s=5;var n=this.value,o=e.search(n,{limit:s,enrich:!0});const t=new Map;for(const e of o.flatMap(e=>e.result)){if(t.has(e.doc.href))continue;t.set(e.doc.href,e.doc)}if(suggestions.innerHTML="",suggestions.classList.remove("d-none"),t.size===0&&n){const e=document.createElement("div");e.innerHTML=`No results for "<strong>${n}</strong>"`,e.classList.add("suggestion__no-results"),suggestions.appendChild(e);return}for(const[r,a]of t){const n=document.createElement("div");suggestions.appendChild(n);const e=document.createElement("a");e.href=r,n.appendChild(e);const o=document.createElement("span");o.textContent=a.title,o.classList.add("suggestion__title"),e.appendChild(o);const i=document.createElement("span");if(i.textContent=a.description,i.classList.add("suggestion__description"),e.appendChild(i),suggestions.appendChild(n),suggestions.childElementCount==s)break}}})()